import React, { useState, useEffect } from "react";
import "./style.css";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";
// import 'bootstrap/dist/css/bootstrap.min.css';

import AgregarProducto from "./AgregarProductos";
import EditarProducto from "./EditarProducto";
import EliminarProducto from "./EliminarProducto";
import Swal from 'sweetalert2';

const styleBody = {
  backgroundColor: "#7A0244",
  color: "#fff",
  paddingTop: "5rem",
  paddingBottom: "5rem",
};

const card = {
  backgroundColor: "#424242",
  borderColor: "#333",
  borderRadius: "1rem",
  boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
  padding: "2rem",
  margin: "auto",
  maxWidth: "65rem",
};

const cardHeader = {
  borderBottom: "none",
  textAlign: "center",
};

const CrudProductos = () => {

  const [articulos, setArticulos] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [recuperado, setRecuperado] = useState(false);
  const [mostrarComponente, setMostrarComponente] = useState(null);
  const [prodEditando, setProdEditando] = useState(null);
  const [prodEliminando, setProdEliminando] = useState(null);

  const handleEditar = (producto) => {
    setProdEditando(producto);
    setMostrarComponente("editar");
    setRecuperado(!recuperado);
  };
  const handleEliminar = (producto) => {
    setProdEliminando(producto);
    setMostrarComponente("eliminar");
  };

  const confirmarEliminar = async () => {
    setCargando(true);
    const response = await fetch(
      `https://api-rest-luis-r45f.vercel.app/products/ID${prodEliminando._id}`,
      {
        method: "DELETE",
      }
    );
    const data = await response.json();
    setArticulos(articulos.filter((productos) => articulos._id !== data._id));
    setProdEliminando(null);
    setCargando(false);
    setRecuperado(true);
    setMostrarComponente(null);
  };

  useEffect(() => {
    async function fetchData() {
      setCargando(true);
      const response = await fetch(
        "https://api-rest-luis-r45f.vercel.app/products"
      );
      const data = await response.json();
      setArticulos(data);
      setCargando(false);
      setRecuperado(true);
    }
    fetchData();
  }, [recuperado]);


  return (
    <div>
      <Header />
      <div style={styleBody}>
        <div style={card}>
          <div style={cardHeader}>
            <h1>Gestor de Productos</h1>
            <button
              className="btn btn-success"
              onClick={() => setMostrarComponente("agregar")}
            >
              Agregar Producto
            </button>
          </div>
          {cargando ? (
            <div>Cargando productos...</div>
          ) : mostrarComponente === "editar" ? (
            <EditarProducto
              articulos={prodEditando}
              guardarProdEditando={setProdEditando}
              guardarRecuperado={setRecuperado}
              cerrarComponente={() => setMostrarComponente(null)}
            />
          ) : mostrarComponente === "eliminar" ? (
            <EliminarProducto
              articulos={prodEliminando}
              confirmarEliminar={confirmarEliminar}
              cancelarEliminar={() => setMostrarComponente(null)}
            />
          ) : mostrarComponente === "agregar" ? (
            <AgregarProducto
              guardarRecuperado={setRecuperado}
              cerrarComponente={() => setMostrarComponente(null)}
            />
          ) : (
            <div className="container">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Nombre</th>
                    <th>Descripcion</th>
                    <th>Precio</th>
                    <th>Imagen</th>
                    <th>Categoria</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {articulos.map((art) => (
                    <tr key={art._id}>
                      <td>{art.name}</td>
                      <td>{art.description}</td>
                      <td>{art.price}</td>
                      <td><img src={ art.image.secure_url} width="200px"/></td>
                      <td>{art.categorie}</td>
                      <td>
                        <button
                          className="btn btn-primary mr-2"
                          onClick={() => handleEditar(art)}
                        >
                          Editar
                        </button>
                        <button
                          className="btn btn-danger"
                          onClick={() => handleEliminar(art)}
                        >
                          Eliminar
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default CrudProductos;
