import React, { useState, useEffect } from "react";
import "./style.css";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";
import AgregarProducto from "./AgregarProductos";
import EditarProducto from "./EditarProducto";
import EliminarProducto from "./EliminarProducto";
import Swal from 'sweetalert2';

const styleBody = {
  backgroundColor: "#7A0244",
  color: "#fff",
  paddingTop: "5rem",
  paddingBottom: "5rem",
};

const card = {
  backgroundColor: "#424242",
  borderColor: "#333",
  borderRadius: "1rem",
  boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
  padding: "2rem",
  margin: "auto",
  maxWidth: "65rem",
};

const cardHeader = {
  borderBottom: "none",
  textAlign: "center",
};

const CrudProductos = () => {

  const [articulos, setArticulos] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [recuperado, setRecuperado] = useState(false);
  const [mostrarComponente, setMostrarComponente] = useState(null);
  const [prodEditando, setProdEditando] = useState(null);
  const [prodEliminando, setProdEliminando] = useState(null);



  const handleEditar = (producto) => {
    setProdEditando(producto);
    setMostrarComponente("editar");
  };

  const handleEliminar = (producto) => {
    setProdEliminando(producto);
    setMostrarComponente("eliminar");
  };

  const handleEditarProducto = async (producto, imagen) => {
    const formData = new FormData();
    formData.append('name', producto.name);
    formData.append('description', producto.description);
    formData.append('price', producto.price);
    formData.append('categorie', producto.categorie);
    formData.append('image', imagen);
    try {
      const response = await fetch(`https://api-rest-luis-r45f.vercel.app/products/${producto._id}`, {
        method: 'PUT',
        body: formData
      });
      const data = await response.json();
      if (response.status === 200) {
        Swal.fire({
          icon: "success",
          title: "Producto actualizado",
          text: "El producto ha sido actualizado exitosamente",
        });
        setRecuperado(!recuperado);
        setProdEditando(null);
        setMostrarComponente(null);
      } else {
        Swal.fire({
          icon: "error",
          title: "Error al actualizar",
          text: "Ha ocurrido un error al actualizar el producto. Por favor, inténtalo de nuevo.",
        });
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleEliminarProducto = async (producto) => {
    try {
      const response = await fetch(`https://api-rest-luis-r45f.vercel.app/products/${producto._id}`, {
        method: 'DELETE'
  });
    if (response.status === 200) {
      Swal.fire({
        icon: "success",
        title: "Producto eliminado",
        text: "El producto ha sido eliminado exitosamente",
      });
      setRecuperado(!recuperado);
      setProdEliminando(null);
      setMostrarComponente(null);
    } else {
      Swal.fire({
        icon: "error",
        title: "Error al eliminar",
        text: "Ha ocurrido un error al eliminar el producto. Por favor, inténtalo de nuevo.",
      });
    }
  } catch (error) {
    console.log(error);
  }
};

useEffect(() => {
  const obtenerArticulos = async () => {
    try {
      const response = await fetch("https://api-rest-luis-r45f.vercel.app/products");
      const data = await response.json();
      setArticulos(data);
      setCargando(false);
    } catch (error) {
      console.log("error obtener los productos", error);
    }
  };
  obtenerArticulos();
}, [recuperado]);

return (
  <div style={styleBody}>
    <Header />
    <div style={card}>
      <h2 style={cardHeader}>CRUD Productos</h2>
      <button className="btn btn-primary" onClick={() => setMostrarComponente("agregar")}>
        Agregar Producto
      </button>
      {cargando ? (
        <p>Cargando productos...</p>
      ) : (
        <table className="table table-dark table-striped">
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Descripción</th>
              <th>Precio</th>
              <th>Categoría</th>
              <th>Imagen</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {articulos.map((producto) => (
              <tr key={producto._id}>
                <td>{producto.name}</td>
                <td>{producto.description}</td>
                <td>{producto.price}</td>
                <td><img src={ producto.image.secure_url} width="100px"/></td>
                <td>{producto.categorie}</td>
                <td>
                  <button className="btn btn-warning" onClick={() => handleEditar(producto)}>
                    Editar
                  </button>{" "}
                  <button className="btn btn-danger" onClick={() => handleEliminar(producto)}>
                    Eliminar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      {mostrarComponente === "agregar" && (
        <AgregarProducto
          onAgregar={() => setRecuperado(!recuperado)}
          onCancel={() => setMostrarComponente(null)}
        />
      )}
      {mostrarComponente === "editar" && (
        <EditarProducto
          producto={prodEditando}
          onEditar={(prod, img) => handleEditarProducto(prod, img)}
          onCancel={() => setMostrarComponente(null)}
        />
      )}
      {mostrarComponente === "eliminar" && (
        <EliminarProducto
          producto={prodEliminando}
          onEliminar={(prod) => handleEliminarProducto(prod)}
          onCancel={() => setMostrarComponente(null)}
        />
      )}
    </div>
    <Footer />
  </div>
);
};

export default CrudProductos;
