import React, { useState, useEffect } from "react";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";
import Panel from "../panel/Panel";
import "./Login.css";
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [correo, setCorreo] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (event) => {
    event.preventDefault();

    const response = await fetch(
      "https://api-rest-proyecto.onrender.com/valUsers",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        //   body: JSON.stringify({ correo, password })
        body: JSON.stringify({
          email: correo,
          password: password,
        }),
      }
    );
    // console.log(response);

    const data = await response.json();
    console.log(data);
    // Manejar la respuesta del API REST aquí
    if (data.message == "Correo o contraseña incorrectos") {
      console.log("Correo o contraseña incorrectos");
      Swal.fire({
        title: handleBoleano ? "Registrado con exito" : "Error en el registro",
        text: handleBoleano ? "Bienvenido " + nombre : errorMessage,
        icon: handleBoleano ? "success" : "error",
        confirmButtonText: "Cerrar",
      });
    } else {
      console.log("Hao");
      navigate('/panel');
    }
  };

  return (
    <div>
      <Header />
      <div className="content">
        <div className="contact-wrapper animated bounceInUp">
          <div className="contact-form">
            {/* <!-- action="validacion.php" --> */}
            <form onSubmit={handleLogin}>
              <h3>Inicio de sesión </h3>
              <p className="box">
                <label htmlFor="email">Correo electrónico: </label>
                <input
                  placeholder="Ingresa tu nombre"
                  type="email"
                  name="usuario"
                  value={correo}
                  onChange={(event) => setCorreo(event.target.value)}
                />
              </p>
              <p className="box">
                <label htmlFor="password">Contraseña</label>
                <input
                  placeholder="Ingresa tu contraseña"
                  type="password"
                  name="pass"
                  value={password}
                  onChange={(event) => setPassword(event.target.value)}
                />
              </p>
              <br />
              <p className="box form ">
                <button type="submit" values="Login">
                  Enviar
                </button>
              </p>
            </form>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Login;
