import React, { useState, useEffect } from 'react';

function RealizarPedido() {
  const [cantidad, setCantidad] = useState(1);

  const handleCantidadChange = (event) => {
    setCantidad(event.target.value);
  };

  const handlePedidoSubmit = (event) => {
    event.preventDefault();
    // Aquí deberías enviar el formulario con los datos a tu backend
  };

  const paypalOptions = {
    clientId: 'AWj2UpMulBmJ7vtmAfb4bs1BF2gVfvnl3_ltY2wcp_O1wgMt91xwhDfkx3pT4lZg9S1d08JzV4hoWJA8',
    currency: 'MXN',
  };

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://www.paypal.com/sdk/js?client-id=${paypalOptions.clientId}&currency=${paypalOptions.currency}`;
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    }
  }, [paypalOptions]);

  const handlePaypalPayment = (details, actions) => {
    actions.order.capture().then(function(detalles){
      alert("Pago completado :)");
      window.location.href="./platillos.php";
    });
  };

  return (
    <>
      <h1 className="title">Realiza tu pedido</h1>

      <div className="container">
        <form onSubmit={handlePedidoSubmit}>
          <div className="card">
            <h3>Título del platillo</h3>
            <p>La existencia es: 10</p>
            <p>Selecciona la cantidad a comprar:</p>
            <input type="number" value={cantidad} onChange={handleCantidadChange} />
            <button type="submit">Realizar Pedido</button>
            <div className="pago" id="paypal-button-container"></div>

            {
              paypal.Buttons({
                style:{
                  color:'blue',
                  shape: 'pill',
                  label: 'pay'
                },
                createOrder: function(data, actions){
                  return actions.order.create({
                    purchase_units: [{
                      amount:{
                        value: cantidad
                      }
                    }]
                  });
                },
                onApprove: handlePaypalPayment,
                onCancel: function(data){
                  alert("Pago cancelado");
                  console.log(data);
                }
              }).render('#paypal-button-container')
            }
          </div>
        </form>
      </div>
    </>
  );
}

export default RealizarPedido;
