import React, {useState} from "react";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";
import "../login/Login.css";

import Swal from 'sweetalert2';

const Registro = () => {

  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');
  const [error, setError] = useState('');
  const [passwordError, setPasswordError] = useState("");



  const validaciones=()=>{
    const mayusculas = /[A-Z]/;
    const minusculas = /[a-z]/;
    const numeros = /\d/;
    const caracteres = /[!@#$%^&*()\-_=+{};:,<.>]/;

    const errors = [];

    if (!mayusculas.test(password)) {
      errors.push("La contraseña debe tener al menos una letra mayúscula.");
    }

    if (!minusculas.test(password)) {
      errors.push("La contraseña debe tener al menos una letra minúscula.");
    }

    if (!numeros.test(password)) {
      errors.push("La contraseña debe tener al menos un número.");
    }

    if (!caracteres.test(password)) {
      errors.push("La contraseña debe tener al menos un carácter especial.");
    }

    if (password != password2) {
      errors.push("Las contraseñas no coinciden.");
    }

    if (errors.length > 0) {
      setPasswordError(errors.join(" "));
      Swal.fire({
        title: "Error",
        text: passwordError,
        icon: "error",
        confirmButtonText: "Cerrar",
      });

      // setPasswordError("");
      return false;
    } else {
      setPasswordError("");
      return true;
    }
  }

  const handleRegistrar= async (cont)=>{
    cont.preventDefault();

    if (validaciones()) {
      const respuesta = await fetch ("https://api-rest-proyecto.onrender.com/users", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            name: nombre, 
            email: correo, 
            password: password
          })
        }
      );

      const data = await respuesta.json();
      console.log(data);
    }

  }
  

  return (
    <div>
      <Header />
      <div className="content">
        <div className="contact-wrapper animated bounceInUp">
          <div className="contact-form">
            <form onSubmit={ handleRegistrar }>
              <h3>Comentario personal </h3>
              <div className="box">
                <label htmlFor="email"> Nombre</label>
                <input
                  type="text"
                  name="usuario"
                  id="usuario"
                  placeholder="Por ejemplo Ana o Esteban"
                  required 
                  maxLength="28"
                  minLength="3"
                  onChange={ (cont) => setNombre(cont.target.value) }
                  value={nombre}
                />

              </div>
              <div className="box">
                <label htmlFor="email"> Correo eletronico</label>
                <input
                  type="email"
                  placeholder="Ingresa tu correo eletronico"
                  id="email"
                  name="email"
                  required maxLength="28"
                  onChange={ (cont) => setCorreo(cont.target.value) }
                  value={correo}
                />

              </div>
              <div className="box">
                <label htmlFor="email"> Contraseña</label>
                <input
                  type="password"
                  name="pass"
                  id="pass"
                  placeholder="Por Ejemplo A123456"
                  required maxLength="28" minLength="8"
                  onChange={ (cont) => setPassword(cont.target.value) }
                  value={password}
                />
              </div>

              <div className="box ">
                <label htmlFor="usuario"> Repite contraseña</label>
                <i className="fa-solid fa-loc "></i>
                <input
                  type="password"
                  name="password2"
                  id="password2"
                  placeholder="Por Ejemplo A123456"
                  required maxLength="28" minLength="8"
                  onChange={ (cont) => setPassword2(cont.target.value) }
                  value={password2}
                />
              </div>
              <br />

              {passwordError && <p style={{ color: 'red' }}>{passwordError}</p>}

              <div className="box form ">
                <button type="submit" className="button">
                  Enviar
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Registro;
