import React, { useState } from "react";

const EditarUsuario = ({ usuario, guardarUsuarioEditando, guardarRecuperado }) => {
const [nombre, setNombre] = useState(usuario.name);
const [email, setEmail] = useState(usuario.email);

const handleSubmit = async (event) => {
event.preventDefault();
const response = await fetch("https://api-rest-luis-r45f.vercel.app/users/${usuario.id}", {
method: "PUT",
headers: {
"Content-Type": "application/json",
},
body: JSON.stringify({ name: nombre, email: email }),
});
const data = await response.json();
guardarUsuarioEditando(null);
guardarRecuperado(false);
};

const handleChangeNombre = (event) => {
setNombre(event.target.value);
};

const handleChangeEmail = (event) => {
setEmail(event.target.value);
};

return (
<div className="container">
<h2>Editar usuario</h2>
<form onSubmit={handleSubmit}>
<div className="form-group">
<label htmlFor="nombre">Nombre</label>
<input
         type="text"
         className="form-control"
         id="nombre"
         value={nombre}
         onChange={handleChangeNombre}
       />
</div>
<div className="form-group">
<label htmlFor="email">Email</label>
<input
         type="email"
         className="form-control"
         id="email"
         value={email}
         onChange={handleChangeEmail}
       />
</div>
<button type="submit" className="btn btn-primary">
Guardar cambios
</button>
</form>
</div>
);
};

export default EditarUsuario;
