import React, { useState, useEffect } from "react";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";
import EditarUsuario from "./EditarUsuario";
import EliminarUsuario from "./EliminarUsuario";

const Crud = () => {
  const styleBody = {};

  const [users, setUsers] = useState([]);
  const [cargando, setCargando] = useState(false);
  const [recuperado, setRecuperado] = useState(false);
  const [usuarioEditando, setUsuarioEditando] = useState(null);
  const [usuarioEliminando, setUsuarioEliminando] = useState(null);

  const handleEditar = (usuario) => {
    setUsuarioEditando(usuario);
  };

  const handleEliminar = (usuario) => {
    setUsuarioEliminando(usuario);
  };

  const confirmarEliminar = async () => {
    const response = await fetch(
      `https://api-rest-luis-r45f.vercel.app/users/${usuarioEliminando._id}`,
      {
        method: "DELETE",
      }
    );
    const data = await response.json();
    setRecuperado(!recuperado);
  };

  useEffect(() => {
    async function fetchData() {
      setCargando(true);
      const response = await fetch("https://api-rest-luis-r45f.vercel.app/users");
      const data = await response.json();
      setUsers(data);
      setCargando(false);
      setRecuperado(true);
    }
    fetchData();
  }, [recuperado]);

  return (
    <div>
      <Header />
      <div style={styleBody}>
        <div style={card}>
          <div style={cardHeader}>
            <h1>Gestor de USUARIOS</h1>
          </div>
          {cargando ? (
            <div>Cargando usuarios...</div>
          ) : usuarioEditando ? (
            <EditarUsuario
              usuario={usuarioEditando}
              guardarUsuarioEditando={setUsuarioEditando}
              guardarRecuperado={setRecuperado}
            />
          ) : (
            <div className="container">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Nombre</th>
                    <th>Email</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user._id}>
                      <td>{user._id}</td>
                      <td>{user.name}</td>
                      <td>{user.email}</td>
                  <td>
                    <button
                      className="btn btn-info"
                      onClick={() => handleEditar(user)}
                    >
                      Editar
                    </button>{" "}
                    <button
                      className="btn btn-danger"
                      onClick={() => handleEliminar(user)}
                    >
                      Eliminar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  </div>
  <Footer />
  {usuarioEliminando && (
    <EliminarUsuario
      usuario={usuarioEliminando}
      confirmarEliminar={confirmarEliminar}
      cancelarEliminar={() => setUsuarioEliminando(null)}
    />
  )}
</div>
);
};

export default Crud;
