import React, { useState } from "react";
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';

const EditarProducto = ({ articulos, guardarProdEditando, guardarRecuperado, cerrarComponente }) => {
  const [id, setId] = useState(articulos._id);
  const [nombre, setNombre] = useState(articulos.name);
  const [descripcion, setDescripcion] = useState(articulos.description);
  const [precio, setPrecio] = useState(articulos.price);
  const [imagen, setImagen] = useState(articulos.image.secure_url);
  const [categoria, setCategoria] = useState(articulos.categorie);

  const navigate = useNavigate();

  // const handleSubmit = async (event) => {
  //   event.preventDefault();
  //   const response = await fetch(`https://api-rest-luis-r45f.vercel.app/products/${id}`, {
  //     method: "PUT",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify({ name: nombre, description: descripcion, price: precio, categorie: categoria}),
  //   });
  //   const data = await response.json();
  //   // console.log(data)

  //   Swal.fire({
  //     title: "Actualizado",
  //     text: "Documentos actualizados",
  //     icon: "success",
  //     confirmButtonText: "Cerrar",
  //   });
  //   guardarProdEditando(null);
  //   // history.push('/crud');
  //   guardarRecuperado(false);
  //   navigate("/crudProductos");
  //   // return <Navigate to={"/crud"} />;
    
  // };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData(); // Crea un nuevo objeto FormData
    formData.append('name', nombre);
    formData.append('description', descripcion);
    formData.append('price', precio);
    formData.append('categorie', categoria);
    formData.append('image', imagen); // Agrega la imagen al objeto FormData

    const response = await fetch(`https://api-rest-proyecto.onrender.com/products/${id}`, {
      method: "PUT",
      body: formData // Usa el objeto FormData como cuerpo de la solicitud
    });
    const data = await response.json();
    // console.log(data);
    Swal.fire({
      title: "Actualizado",
      text: "Documento Actualizado",
      icon: "success",
      confirmButtonText: "Cerrar",
    });
    guardarProdEditando(null);
    // history.push('/crud');
    guardarRecuperado(false);
    navigate("/crudProductos");
  };

  const handleChangeNombre = (event) => {
    setNombre(event.target.value);
  };

  const handleChangeDescripcion = (event) => {
    setDescripcion(event.target.value);
  };

  const handleChangePrice = (event) => {
    setPrecio(event.target.value);
  };

  const handleChangeImage = (event) => {
    setImagen(event.target.files[0]);
  };

  const handleChangeCategoria = (event) => {
    setCategoria(event.target.value);
  };

  const handleCancelar = () => {
    cerrarComponente();
  };
  return (
<div className="container">
      <h2>Editar Producto</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="id">ID:</label>
          <input type="text" id="id" name="id" value={id} readOnly />
        </div>
        <div className="form-group">
          <label htmlFor="nombre">Producto:</label>
          <input
            type="text"
            id="nombre"
            name="nombre"
            value={nombre}
            onChange={handleChangeNombre}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Descripcion:</label>
          <input
            type="text"
            id="descripcion"
            name="descripcion"
            value={descripcion}
            onChange={handleChangeDescripcion}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Precio:</label>
          <input
            type="text"
            id="precio"
            name="precio"
            value={precio}
            onChange={handleChangePrice}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Imagen:</label>
          <img src={ imagen } width="200px"/>
          <input
            type="text"
            id="precio"
            name="precio"
            value={imagen}
            onChange={handleChangeImage}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Categoria:</label>
          <input
            type="text"
            id="categoria"
            name="categoria"
            value={categoria}
            onChange={handleChangeCategoria}
          />
        </div>
        <button type="submit" className="btn btn-primary mr-2">
          Guardar
        </button>
        <button type="button" className="btn btn-secondary" onClick={handleCancelar}>
          Salir
        </button>
      </form>
    </div>
  );
};
export default EditarProducto
