import React, { createContext, useState } from "react";

// Creamos un contexto de sesión
export const SessionContext = createContext();

// Creamos el proveedor del contexto de sesión
export const SessionProvider = ({ children }) => {
  const [user, setUser] = useState(null); // Estado para el usuario actual

  const login = (user) => {
    setUser(user);
  };

  const logout = () => {
    setUser(null);
  };

  // Pasamos los valores de usuario, login y logout al proveedor del contexto de sesión
  return (
    <SessionContext.Provider value={{ user, login, logout }}>
      {children}
    </SessionContext.Provider>
  );
};
