import React from 'react'
import { useNavigate } from 'react-router-dom';

const CheckCorreo = () => {
  const cardHeader = {
    borderBottom: "none",
    textAlign: "center",
  };

  const navigate = useNavigate();

  function handleRegistroClick() {
    navigate('/registro');
  }

  function handleIngresoClick() {
    navigate('/login');
  }

  return (
    <div className='BodyCorreo'>
      <div class="container">
        <div class="row justify-content-center">
          <div class="col-lg-6">
            <div class="card">
              <div class="card-header">
                <h4>Restablecer contraseña</h4>
              </div>

              {/* <?php if(isset($error)): ?>
            <div class="alert alert-danger" role="alert">
              <?php echo $error; ?>
            </div>
            <?php endif; ?> */}

              <div class="card-body">
                <form action="<?= base_url('/check') ?>" method="post">
                  <div class="form-group">
                    <label for="correo">Ingresa tu email abajo para restablecer la contraseña</label>
                    <input type="emial" name="correo" class="form-control" id="correo" aria-describedby="emailHelp" placeholder="Ingrese su correo electronico" />
                  </div>
                  <button type="submit" class="btn btn-primary btn-block">Verificar</button>
                </form>
              </div>
              <div class="card-footer">
                ¿No tienes una cuenta? <a onClick={handleRegistroClick}>Regístrate aquí</a>
                ¿Ya tienes una cuenta? <a onClick={handleIngresoClick}>Inicia sesión</a>
              </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default CheckCorreo
