import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../estructura/Header";
import Footer from "../estructura/Footer";

const BodyCorreo = {
  backgroundColor: "#343a40",
  color: "#fff",
  paddingTop: "5rem",
};

const cardCorreo = {
  backgroundColor: "#424242",
  borderColor: "#333",
  borderRadius: "1rem",
  boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
  padding: "2rem",
  margin: "auto",
  maxWidth: "25rem",
};

const card_headerCorreo = {
  borderBottom: "none",
  textAlign: "center",
};

const card_footerCorreo = {
  borderTop: "none",
  textAlign: "center",
};

const form_controlCorreo = {
  backgroundColor: "#545454",
  border: "none",
  borderRadius: "0.5rem",
  color: "#fff",
  marginBottom: "1rem",
  padding: "0.75rem",
};

const btn_primaryCorreo = {
  backgroundColor: "#007bff",
  borderColor: "#007bff",
  borderRadius: "0.5rem",
  fontSize: "1rem",
  fontWeight: "600",
  padding: "0.75rem 2rem",
};

const CheckCorreo = () => {
  const [correo, setCorreo] = useState("");

  const navigate = useNavigate();

  function handleRegistroClick() {
    navigate("/registro");
  }

  function handleIngresoClick() {
    navigate("/login");
  }

  const codigo=({id,code})=>{
    navigate.navigate('CodeRecu',{
        id: id,
        codeUser: code
    })
  }

  const handleCorreo = async (event) => {
    event.preventDefault();

    const response = await fetch(
      "https://api-rest-luis-r45f.vercel.app/recuPassword",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: correo,
        }),
      }
    );

    const data = await response.json();
    console.log(data);

    if (
      data.message == "El correo electrónico proporcionado no está registrado"
    ) {
      console.log("El correo electrónico proporcionado no está registrado");
      Swal.fire({
        title: "Error",
        text: "El correo electrónico proporcionado no está registrado",
        icon: "error",
        confirmButtonText: "Cerrar",
      });
    } else {
      // console.log(data.name+" "+data.email);
      // const user = { usuario: data.name, correo: data.email };
      // navigate('/panel', { state: user });
      const response2 = await fetch(
        "https://api-rest-luis-r45f.vercel.app/usersEmail",{
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            email: email,
          }),
      });

      const data2 = await response2.json()
      codigo({ id: data2._id, code: data2.resetToken })
    }
  };

  return (
    <div>
      <Header />
      <div style={BodyCorreo}>
        <div class="container">
          <div class="row justify-content-center">
            <div class="col-lg-6">
              <div style={cardCorreo}>
                <div style={card_headerCorreo}>
                  <h4>Restablecer contraseña</h4>
                </div>
                <div class="card-body">
                  <form onSubmit={handleCorreo}>
                    <div class="form-group">
                      <label for="correo">
                        Ingresa tu email abajo para restablecer la contraseña
                      </label>
                      <input
                        type="emial"
                        name="correo"
                        style={form_controlCorreo}
                        id="correo"
                        aria-describedby="emailHelp"
                        placeholder="Ingrese su correo electronico"
                        value={correo}
                        onChange={(event) => setCorreo(event.target.value)}
                      />
                    </div>
                    <button type="submit" class="btn btn-primary btn-block">
                      Verificar
                    </button>
                  </form>
                </div>
                <div class="card-footer">
                  ¿No tienes una cuenta?{" "}
                  <a onClick={handleRegistroClick}>Regístrate aquí</a>
                  <br />
                  ¿Ya tienes una cuenta?{" "}
                  <a onClick={handleIngresoClick}>Inicia sesión</a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default CheckCorreo;
