import React, { useState } from "react";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";

import "./Login.css";
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [correo, setCorreo] = useState("");
  const [password, setPassword] = useState("");

  function handleRegistroClick() {
    navigate('/registro');
  }

  function handleIngresoClick() {
    navigate('/checkcorreo');
  }

  const handleLogin = async (event) => {
    event.preventDefault();

    const response = await fetch("https://api-rest-proyecto.onrender.com/valUsers", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      //   body: JSON.stringify({ correo, password })
      body: JSON.stringify({
        email: correo,
        password: password,
      }),
    }
    );
    // console.log(response);

    const data = await response.json();
    // console.log(data);
    // Manejar la respuesta del API REST aquí
    if (data.message == "Correo o contraseña incorrectos") {
      console.log("Correo o contraseña incorrectos");
      Swal.fire({
        title: "Error",
        text: "Correo o contraseña incorrectos",
        icon: "error",
        confirmButtonText: "Cerrar",
      });
    } else {
      // console.log(data.name+" "+data.email);
      const user = { usuario: data.name, correo: data.email };
      navigate('/panel', { state: user });
    }
  };

  return (
    <div>
      <Header />
      <div className="content">
        <div className="contact-wrapper">
          <div className="contact-form">
            {/* <!-- action="validacion.php" --> */}
            <form onSubmit={handleLogin} className="formLog">
              <h3>Inicio de sesión </h3>
              <p className="box">
                <label htmlFor="email" className="labelLog">Correo electrónico: </label>
                <input
                  type="email"
                  placeholder="Ingresa tu nombre"
                  name="email"
                  id="email"
                  required maxLength="28"
                  value={correo}
                  onChange={(event) => setCorreo(event.target.value)}
                />
              </p>
              <p className="box">
                <label htmlFor="password">Contraseña</label>
                <input
                  type="password"
                  placeholder="Ingresa tu contraseña"
                  name="password"
                  id="password"
                  required maxLength="28"
                  // minLength="8"
                  value={password}
                  onChange={(event) => setPassword(event.target.value)}
                />
              </p>
              <br />
              <p className="box form ">
                <button type="submit" values="Login">
                  Enviar
                </button>
              </p>
            </form>
            <div className="card-footer">
              ¿No tienes una cuenta? <a onClick={handleRegistroClick}>Regístrate aquí</a>
              <br />
              ¿Olvidaste tu contraseña? <a onClick={handleIngresoClick}>Ingresa aquí</a>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Login;
