import React, { useState } from 'react';

function RealizarPedido() {
  const [amount, setAmount] = useState(0);
  const [showButton, setShowButton] = useState(false);

  const handleChange = (event) => {
    setAmount(event.target.value);
    setShowButton(event.target.value > 0);
  };

  const paypalOptions = {
    clientId: 'https://www.paypal.com/sdk/js?client-id=AWj2UpMulBmJ7vtmAfb4bs1BF2gVfvnl3_ltY2wcp_O1wgMt91xwhDfkx3pT4lZg9S1d08JzV4hoWJA8&currency=MXN',
    currency: 'MXN',
  };

  const handlePaypalPayment = (details, actions) => {
    return actions.order.create({
      purchase_units: [{
        amount: {
          value: amount
        }
      }]
    }).then(function(orderID) {
      return actions.order.capture().then(function(details) {
        alert('Pago completado');
      });
    });
  };

  return (
    <div>
      <h1>Realizar pago con PayPal</h1>
      <p>Ingresa la cantidad que deseas pagar:</p>
      <input type="number" min="0" value={amount} onChange={handleChange} />
      {showButton && (
        <>
        <div id="paypal-button-container"></div>
        <script src={`https://www.paypal.com/sdk/js?client-id=${paypalOptions.clientId}&currency=${paypalOptions.currency}`}></script>
        {
          paypal.Buttons({
            style:{
              color:'blue',
              shape: 'pill',
              label: 'pay'
            },
            createOrder: function(data, actions){
              return actions.order.create({
                purchase_units: [{
                  amount:{
                    value: amount
                  }
                }]
              });
            },
            onApprove: handlePaypalPayment,
            onCancel: function(data){
              alert("Pago cancelado");
              console.log(data);
            }
          }).render('#paypal-button-container')
        }
        </>
      )}
    </div>
  );
}

export default RealizarPedido;
