import React, { useState, useEffect } from "react";
import "./style.css";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";

const CrudProductos = () => {
  const styleBody = {
    backgroundColor: "#7A0244",
    color: "#fff",
    paddingTop: "5rem",
    paddingBottom: "5rem",
  };

  const card = {
    backgroundColor: "#424242",
    borderColor: "#333",
    borderRadius: "1rem",
    boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
    padding: "2rem",
    margin: "auto",
    maxWidth: "65rem",
  };

  const cardHeader = {
    borderBottom: "none",
    textAlign: "center",
  };

  const [articulos, setArticulos] = useState([]);
  const [recuperado, setRecuperado] = useState(false);

  useEffect(() => {
    fetch("https://api-rest-luis-r45f.vercel.app/products")
      .then((response) => {
        return response.json();
      })
      .then((articulos) => {
        setArticulos(articulos);
        setRecuperado(true);
      });
  }, []);

  if (recuperado) {
    return (
      <div>
        <Header />
        <div style={styleBody}>
          <div style={card}>
            <div style={cardHeader}>
              <h1>Tabla de PRODUCTOS</h1>
            </div>
            <div className="container">
              <div className="row mx-auto justify-content-between">
                <div className="col-3">
                  <a
                    href="<?= base_url('/agregarProd')?>"
                    className="btn btn-success"
                    role="button"
                  >
                    Añadir +
                  </a>
                </div>
              </div>

              <div className="card-body">
                <div className="row pt-3 mx-auto justify-content-center">
                  <div className="col-8">
                    <div className="table-responsive">
                      <table className="table">
                        <thead className="thead-dark">
                          <tr>
                            <th scope="col">#ID</th>
                            <th scope="col">Nombre</th>
                            <th scope="col">Tipo</th>
                            <th scope="col">Descripcion</th>
                            <th scope="col">Marca</th>
                            <th scope="col">Precio</th>
                            <th scope="col">Modelo</th>
                            <th></th>
                            <th></th>
                          </tr>
                        </thead>

                        <tbody>
                          {articulos.map((art) => {
                            return (
                              <tr>
                                <th scope="row">{art._id}</th>
                                <td>{art.name}</td>
                                <td></td>
                                <td>{art.description}</td>
                                <td></td>
                                <td>{art.price}</td>
                                <td></td>
                                <td>
                                  <a
                                    className="btn btn-warning"
                                    href="<?= base_url('/editarP') ?>?id=<?= $row['idProducto'] ?>"
                                    role="button"
                                  >
                                    Editar
                                  </a>
                                </td>
                                <td>
                                  <a
                                    className="btn btn-danger"
                                    href="<?= base_url('/eliminarP') ?>?id=<?= $row['idProducto'] ?>"
                                    role="button"
                                  >
                                    Eliminar
                                  </a>
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  } else return <div>recuperando datos...</div>;
};

export default CrudProductos;
