import React, { useState } from "react";
import Swal from 'sweetalert2';
// import { useNavigate } from 'react-router-dom';
// import { useHistory } from 'react-router-dom';
// import { Navigate } from 'react-router-dom';

const EditarUsuario = ({ articulos, guardarProdEditando, guardarRecuperado, cerrarComponente }) => {
  const [id, setId] = useState(articulos._id);
  const [nombre, setNombre] = useState(articulos.name);
  const [descripcion, setDescripcion] = useState(articulos.description);
  const [precio, setPrecio] = useState(articulos.price);
  const [categoria, setCategoria] = useState(articulos.categorie);
  const [imagen, setImagen] = useState(articulos.image);
  // const history = useHistory();
  // const navigate = useNavigate();


  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData(); // Crea un nuevo objeto FormData
    formData.append('name', nombre);
    formData.append('description', descripcion);
    formData.append('price', precio);
    formData.append('categorie', categoria);
    formData.append('image', imagen); // Agrega la imagen al objeto FormData

    // console.log(formData);
    const response = await fetch(`https://api-rest-proyecto.onrender.com/updateProducts/${id}`, {
      method: "PUT",
      body: formData
    });
    const data = await response.json();
    console.log(data)

    // Swal.fire({
    //   title: "Actualizado",
    //   text: "Documentos actualizados",
    //   icon: "success",
    //   confirmButtonText: "Cerrar",
    // });
    guardarProdEditando(null);
    // history.push('/crud');
    guardarRecuperado(false);
    // navigate("/crud");
    // return <Navigate to={"/crud"} />;
    
  };
  

  const handleChangeNombre = (event) => {
    setNombre(event.target.value);
  };

  const handleChangeDescripcion = (event) => {
    setDescripcion(event.target.value);
  };

  const handleChangePrecio = (event) => {
    setPrecio(event.target.value);
  };
  const handleChangeCategoria = (event) => {
    setCategoria(event.target.value);
  };
  const handleChangeImagen = (event) => {
    setCategoria(event.target.value);
  };


  const handleCancelar = () => {
    cerrarComponente();
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          className="form-control"
          value={nombre}
          onChange={(event) => setNombre(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Descripcion:</label>
        <input
          type="text"
          className="form-control"
          value={descripcion}
          onChange={(event) => setDescripcion(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          className="form-control"
          value={precio}
          onChange={(event) => setPrecio(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Imagen:</label>
        <input
          type="file"
          className="form-control"
          onChange={(event) => setImagen(event.target.files[0])} // Actualiza el estado de "imagen" con el archivo seleccionado
        />
      </div>
      <div className="form-group">
        <label>Categoria:</label>
        <input
          type="text"
          className="form-control"
          value={categoria}
          onChange={(event) => setCategoria(event.target.value)}
        />
      </div>
      <button type="submit" className="btn btn-primary">
        Actualizar
      </button>
      <button type="button" className="btn btn-secondary" onClick={handleCancelar}>
          Salir
        </button>
    </form>
  )
};

export default EditarUsuario;
