import React, { useState, useEffect } from "react";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";
import EditarUsuarioForm from "./EditarUsuarioForm";

const CrudUsuarios = () => {
  const styleBody = {
    backgroundColor: "#7A0244",
    color: "#fff",
    paddingTop: "5rem",
    paddingBottom: "5rem",
  };

  const card = {
    backgroundColor: "#424242",
    borderColor: "#333",
    borderRadius: "1rem",
    boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
    padding: "2rem",
    margin: "auto",
    maxWidth: "65rem",
  };

  const cardHeader = {
    borderBottom: "none",
    textAlign: "center",
  };

  const [usuarios, setUsuarios] = useState([]);
  const [editing, setEditing] = useState(false);
  const [currentUser, setCurrentUser] = useState({ id: null, nombre: "", email: "", telefono: "" });

  useEffect(() => {
    // Aquí deberías hacer una petición a tu API para obtener los usuarios
    // y luego actualizar el estado de usuarios con los datos obtenidos
  }, []);

  const addUser = (usuario) => {
    // Aquí deberías hacer una petición POST a tu API para agregar el nuevo usuario
    // y luego actualizar el estado de usuarios con los datos obtenidos
  };

  const deleteUser = (id) => {
    // Aquí deberías hacer una petición DELETE a tu API para eliminar el usuario con el id dado
    // y luego actualizar el estado de usuarios eliminando el usuario con el id dado
  };

  const updateUser = (id, updatedUser) => {
    // Aquí deberías hacer una petición PUT a tu API para actualizar el usuario con el id dado
    // y luego actualizar el estado de usuarios con los datos obtenidos
  };

  const editRow = (usuario) => {
    setEditing(true);
    setCurrentUser({ id: usuario.id, nombre: usuario.nombre, email: usuario.email, telefono: usuario.telefono });
  };

  const cancelEdit = () => {
    setEditing(false);
    setCurrentUser({ id: null, nombre: "", email: "", telefono: "" });
  };

  return (
    <>
      <Header />
      <div style={styleBody}>
        <div style={card}>
          <div style={cardHeader}>
            <h2>Gestión de usuarios</h2>
          </div>
          <div>
            <EditarUsuarioForm editing={editing} currentUser={currentUser} updateUser={updateUser} cancelEdit={cancelEdit} addUser={addUser} />
          </div>
          <div>
            <h3>Usuarios</h3>
            <table>
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Email</th>
                  <th>Teléfono</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {/* Aquí deberías iterar sobre el estado de usuarios y mostrar cada usuario en una fila de la tabla */}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default CrudUsuarios;
