import React, { useState, useEffect } from "react";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";
import AgregarUsuario from "./AgregarUsuario";
import EditarUsuario from "./EditarUsuario";
import EliminarUsuario from "./EliminarUsuario";
// import 'bootstrap/dist/css/bootstrap.min.css'

const styleBody = {
  backgroundColor: "#7A0244",
  color: "#fff",
  paddingTop: "5rem",
  paddingBottom: "5rem",
};

const card = {
  backgroundColor: "#424242",
  borderColor: "#333",
  borderRadius: "1rem",
  boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
  padding: "20px",
  margin: "auto",
  maxWidth: "65rem",
};

// const tr_td = {
//   color: "#fff",
// };

const cardHeader = {
  borderBottom: "none",
  textAlign: "center",
};

const Usuarios = () => {
  const [users, setUsers] = useState([]);
  const [cargando, setCargando] = useState(false);
  const [recuperado, setRecuperado] = useState(false);
  const [mostrarComponente, setMostrarComponente] = useState(null);
  const [usuarioEditando, setUsuarioEditando] = useState(null);
  const [usuarioEliminando, setUsuarioEliminando] = useState(null);

  const handleEditar = (usuario) => {
    setUsuarioEditando(usuario);
    setMostrarComponente("editar");
    setRecuperado(!recuperado);
  };
  const handleEliminar = (usuario) => {
    setUsuarioEliminando(usuario);
    setMostrarComponente("eliminar");
  };

  const confirmarEliminar = async () => {
    setCargando(true);
    const response = await fetch(
      `https://api-rest-luis-r45f.vercel.app/users/${usuarioEliminando._id}`,
      {
        method: "DELETE",
      }
    );
    const data = await response.json();
    setUsers(users.filter((usuario) => usuario._id !== data._id));
    setUsuarioEliminando(null);
    setCargando(false);
    setRecuperado(true);
    setMostrarComponente(null);
  };

  useEffect(() => {
    async function fetchData() {
      setCargando(true);
      const response = await fetch(
        "https://api-rest-luis-r45f.vercel.app/users"
      );
      const data = await response.json();
      setUsers(data);
      setCargando(false);
      setRecuperado(true);
    }
    fetchData();
  }, [recuperado]);

  return (
    <div>
      <Header />
      <div style={styleBody}>
        <div style={card} className="card">
          <div style={cardHeader}>
            <h1>Gestor de USUARIOS</h1>
          </div>
          <div class="container">
            <div class="row mx-auto justify-content-between">
              <div class="col-3">
                <button
                  className="btn btn-success"
                  onClick={() => setMostrarComponente("agregar")}
                >
                  Agregar Usuario
                </button>
              </div>
            </div>

            {cargando ? (
              <div>Cargando usuarios...</div>
            ) : mostrarComponente === "editar" ? (
              <EditarUsuario
                usuario={usuarioEditando}
                guardarUsuarioEditando={setUsuarioEditando}
                guardarRecuperado={setRecuperado}
                cerrarComponente={() => setMostrarComponente(null)}
              />
            ) : mostrarComponente === "eliminar" ? (
              <EliminarUsuario
                usuario={usuarioEliminando}
                confirmarEliminar={confirmarEliminar}
                cancelarEliminar={() => setMostrarComponente(null)}
              />
            ) : mostrarComponente === "agregar" ? (
              <AgregarUsuario
                guardarRecuperado={setRecuperado}
                cerrarComponente={() => setMostrarComponente(null)}
              />
            ) : (
              <div className="card-body">
                <div class="row pt-3 mx-auto justify-content-center">
                  <div class="col-12 col-md-8">
                    <div class="table-responsive">
                      <table className="table">
                        <thead>
                          <tr style={tr_td}>
                            <th>Nombre</th>
                            <th>Password</th>
                            <th>Email</th>
                            <th>Acciones</th>
                          </tr>
                        </thead>
                        <tbody>
                          {users.map((usuario) => (
                            <tr key={usuario._id} style={tr_td}>
                              <td style={tr_td}>{usuario.name}</td>
                              <td style={tr_td}>{usuario.password}</td>
                              <td style={tr_td}>{usuario.email}</td>
                              <td style={tr_td}>
                                <button
                                  className="btn btn-primary mr-2"
                                  onClick={() => handleEditar(usuario)}
                                >
                                  Editar
                                </button>
                                <button
                                  className="btn btn-danger"
                                  onClick={() => handleEliminar(usuario)}
                                >
                                  Eliminar
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>

            )}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Usuarios;
