import React, { useState, useEffect } from "react";
// import "./style.css";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";

const CrudUsuarios = () => {
  const styleBody = {
    backgroundColor: "#7A0244",
    color: "#fff",
    paddingTop: "5rem",
    paddingBottom: "5rem",
  };

  const card = {
    backgroundColor: "#424242",
    borderColor: "#333",
    borderRadius: "1rem",
    boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
    padding: "2rem",
    margin: "auto",
    maxWidth: "65rem",
  };

  const cardHeader = {
    borderBottom: "none",
    textAlign: "center",
  };

  const [usaurios, setUsuarios] = useState([]);
  const [recuperado, setRecuperado] = useState(false);
  const [cargando, setCargando] = useState(true);

  // useEffect(() => {
  //   fetch("https://api-rest-luis-r45f.vercel.app/users")
  //     .then((users) => {
  //       return users.json();
  //     })
  //     .then((users) => {
  //       setUsuarios(users);
  //       setRecuperado(true);
  //       setCargando(false); // establecer cargando a falso después de que se obtienen los datos
  //     });
  // }, []);

  async function response() {
    const response = await fetch("https://api-rest-luis-r45f.vercel.app/users");
    const data = await response.json();
    // console.log(data);
    setUsuarios(data);
    setRecuperado(true);
    setCargando(false); // establecer cargando a falso después de que se obtienen los datos
  }

  useEffect(() => {
    response();
  }, []);

  const handleUpdate = (id) => {
    // Obtener los datos del producto que se actualizará (en este ejemplo, los datos se tomarán del estado de los componentes)
    const user = {
      name: "pruebaxd1",
      email: "pruebaxd1@gmail.com",
      password: "A1234as@"
    }
  
    fetch(`https://api-rest-luis-r45f.vercel.app/users/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(user)
    })
    .then((response) => {
      alert("Actualizado")
    })
    .catch((error) => {
      // Manejar cualquier error
      console.log(error.message)
    });
  }

  return (
    <div>
      <Header />
      <div style={styleBody}>
        <div style={card}>
          <div style={cardHeader}>
            <h1>Tabla de USUARIOS</h1>
          </div>
          {cargando ? ( // mostrar mensaje de carga si cargando es verdadero
            <div className="row pt-3 mx-auto justify-content-center">
              <div className="col-8">
                <p>Cargando...</p>
              </div>
            </div>
          ) : (
            <div className="container">
              <div className="row mx-auto justify-content-between">
                <div className="col-3">
                  <a
                    href="<?= base_url('/agregarProd')?>"
                    className="btn btn-success"
                    role="button"
                  >
                    Añadir +
                  </a>
                </div>
              </div>

              <div className="card-body">
                <div className="row pt-3 mx-auto justify-content-center">
                  <div className="col-8">
                    <div className="table-responsive">
                      <table className="table">
                        <thead className="thead-dark">
                          <tr>
                            <th scope="col">Nombre</th>
                            {/* <th scope="col">Apellidos</th> */}
                            {/* <th scope="col">Telefono</th> */}
                            {/* <th scope="col">Usuario</th> */}
                            <th scope="col">Correo</th>
                            <th scope="col">Password</th>
                            {/* <th scope="col">TipoUsuario</th> */}
                            {/* <th scope="col">Pregunta</th> */}
                            {/* <th scope="col">Respuesta</th> */}
                            <th></th>
                            <th></th>
                          </tr>
                        </thead>
                        <tbody>
                          {usaurios.map((users) => {
                            return (
                              <tr>
                                <td>{users.name}</td>
                                <td>{users.email}</td>
                                <td>{users.password}</td>
                                <td>
                                <button
                                  className="btn btn-warning"
                                  onClick={() => handleEditar(usuario)}
                                >
                                  Editar
                                </button>
                                </td>
                                <td>
                                  <a
                                    className="btn btn-danger"
                                    // href=""
                                    role="button"
                                  >
                                    Eliminar
                                  </a>
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default CrudUsuarios;
