import React, { useState } from 'react'
import Swal from 'sweetalert2';

const AgregarProductos = ({ guardarRecuperado, cerrarComponente }) => {

  const [nombre, setNombre] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [precio, setPrecio] = useState("");
  const [imagen, setImagen] = useState(null); // Cambiado a null para que sea un objeto File
  const [categoria, setCategoria] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData(); // Crea un nuevo objeto FormData
    formData.append('name', nombre);
    formData.append('description', descripcion);
    formData.append('price', precio);
    formData.append('categorie', categoria);
    formData.append('image', imagen); // Agrega la imagen al objeto FormData

    const response = await fetch("https://api-rest-proyecto.onrender.com/products", {
      method: "POST",
      body: formData // Usa el objeto FormData como cuerpo de la solicitud
    });
    const data = await response.json();
    // console.log(data);
    Swal.fire({
      title: "Insertado",
      text: "Documento insertado",
      icon: "success",
      confirmButtonText: "Cerrar",
    });
    guardarRecuperado((r) => !r);
    cerrarComponente();
  };

  return (
    <div>
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          className="form-control"
          value={nombre}
          onChange={(event) => setNombre(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Descripcion:</label>
        <input
          type="text"
          className="form-control"
          value={descripcion}
          onChange={(event) => setDescripcion(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          className="form-control"
          value={precio}
          onChange={(event) => setPrecio(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Imagen:</label>
        <input
          type="file"
          className="form-control"
          onChange={(event) => setImagen(event.target.files[0])} // Actualiza el estado de "imagen" con el archivo seleccionado
        />
      </div>
      <div className="form-group">
        <label>Categoria:</label>
        <input
          type="text"
          className="form-control"
          value={categoria}
          onChange={(event) => setCategoria(event.target.value)}
        />
      </div>
      <br/>
      <button type="submit" className="btn btn-primary">
        Agregar Usuario
      </button>
      <button type="submit" className="btn btn-primary" onClick={()=>{cerrarComponente()}}>
        Regresar
      </button>
    </form>
    <br/>

    </div>
  )
};

export default AgregarProductos
