import React, { useState } from "react";
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';

const EditarProducto = ({ articulos, guardarProdEditando, guardarRecuperado, cerrarComponente }) => {
  const [id, setId] = useState(articulos._id);
  const [nombre, setNombre] = useState(articulos.name);
  const [descripcion, setDescripcion] = useState(articulos.description);
  const [precio, setPrecio] = useState(articulos.price);
  const [imagen, setImagen] = useState(articulos.image.secure_url);
  const [categoria, setCategoria] = useState(articulos.categorie);

  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('name', nombre);
    formData.append('description', descripcion);
    formData.append('price', precio);
    formData.append('categorie', categoria);
    if (imagen) {
      formData.append('image', imagen);
    }
    try {
      const response = await fetch(`https://api-rest-proyecto.onrender.com/products/${id}`, {
        method: "PUT",
        // headers: {
        //   "Content-Type": "application/json",
        // },
        body: formData
      });
      const data = await response.json();
      console.log(data);
      Swal.fire({
        title: "Actualizado",
        text: "Documento Actualizado",
        icon: "success",
        confirmButtonText: "Cerrar",
      });
      guardarProdEditando(null);
      guardarRecuperado(false);
      navigate("/crudProductos");
    } catch (error) {
      console.error(error);
      Swal.fire({
        title: "Error",
        text: "Hubo un error al actualizar el documento",
        icon: "error",
        confirmButtonText: "Cerrar",
      });
    }
  };

  const handleChangeNombre = (event) => {
    setNombre(event.target.value);
  };

  const handleChangeDescripcion = (event) => {
    setDescripcion(event.target.value);
  };

  const handleChangePrice = (event) => {
    setPrecio(event.target.value);
  };

  const handleChangeImage = (event) => {
    setImagen(event.target.files[0]);
  };

  const handleChangeCategoria = (event) => {
    setCategoria(event.target.value);
  };

  const handleCancelar = () => {
    cerrarComponente();
  };

  return (
    <div className="container">
      <h2>Editar Producto</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="id">ID:</label>
          <input type="text" id="id" name="id" value={id} readOnly />
        </div>
        <div className="form-group">
          <label htmlFor="nombre">Producto:</label>
          <input
            type="text"
            id="nombre"
            name="nombre"
            value={nombre}
            onChange={handleChangeNombre}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Descripcion:</label>
          <input
            type="text"
            id="descripcion"
            name="descripcion"
            value={descripcion}
            onChange={handleChangeDescripcion}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Precio:</label>
          <input
            type="text"
            id="precio"
            name="precio"
            value={precio}
            onChange={handleChangePrice}
          />
        </div>
        <div className="form-group">
          <label htmlFor="    categoria">Categoría:</label>
          <select
            id="categoria"
            name="categoria"
            value={categoria}
            onChange={handleChangeCategoria}
          >
            <option value="Hogar">Hogar</option>
            <option value="Tecnología">Tecnología</option>
            <option value="Moda">Moda</option>
            <option value="Deportes">Deportes</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="imagen">Imagen:</label>
          <input type="file" id="imagen" name="imagen" onChange={handleChangeImage} />
        </div>
        <div className="botones-form">
          <button type="submit" className="btn btn-primary">
            Actualizar
          </button>
          <button type="button" className="btn btn-danger" onClick={handleCancelar}>
            Cancelar
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditarProducto;
