import React, { useState } from 'react'

const AgregarProductos = ({ guardarRecuperado, cerrarComponente }) => {

  const [nombre, setNombre] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [precio, setPrecio] = useState("");
  const [imagen, setImagen] = useState("");
  const [categoria, setCategoria] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    const response = await fetch("https://api-rest-luis-r45f.vercel.app/products", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name: nombre, description: descripcion, price: precio, categorie: categoria}),
    });
    await response.json();
    guardarRecuperado((r) => !r);
    cerrarComponente();
  };

  return (
<form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          className="form-control"
          value={nombre}
          onChange={(event) => setNombre(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Descripcion:</label>
        <input
          type="tetxt"
          className="form-control"
          value={descripcion}
          onChange={(event) => setDescripcion(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          className="form-control"
          value={precio}
          onChange={(event) => setPrecio(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Imagen:</label>
        {/* <input
          type="email"
          className="form-control"
          value={precio}
          onChange={(event) => setPrecio(event.target.value)}
        /> */}
      </div>
      <div className="form-group">
        <label>Categoria:</label>
        <input
          type="text"
          className="form-control"
          value={categoria}
          onChange={(event) => setCategoria(event.target.value)}
        />
      </div>
      <button type="submit" className="btn btn-primary">
        Agregar Usuario
      </button>
    </form>
  )
};

export default AgregarProductos
