import React, { useState, useEffect } from "react"; import Footer from '../../estructura/Footer'
import Header from '../../estructura/Header'
import './Bebidas.css'

const Bebidas = () => {
  const [bebidas, setBebidas] = useState([]);

  async function response() {
    const response = await fetch("https://api-rest-luis-r45f.vercel.app/getProductsCategorie", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      //   body: JSON.stringify({ correo, password })
      body: JSON.stringify({
        categorie: "Bebidas"
      }),
    });
    const data = await response.json();
    // console.log(data);
    setBebidas(data);
  }

  useEffect(() => {
    response();
  }, []);

  return (
    <div>
      <Header />
      <h1 className="title">Bebidas</h1>
      <div className="container">
      {bebidas.map((prod) => {
          return (
            <div className="card">
              <img
                src={prod.image.secure_url}
                alt=""
                className="today-special__img"
              />
              <h2>{prod.name}</h2>
              <input value={prod.description}></input>
              <a>Ver m√°s...</a>
            </div>
          );
        })}
      </div>
      <br /> <br />
    <Footer />
    </div>
  )
}

export default Bebidas
