import React, { useState, useEffect } from "react";
import Swal from 'sweetalert2';

const AgregarUsuario = ({ guardarRecuperado, cerrarComponente }) => {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [email, setEmail] = useState("");
  // const [tipoUsuario, setTipoUsuario] = useState("");
  // const [typeUser, setTypeUser] = useState([]);


    // Carga la lista de categorías cuando se monta el componente
    // useEffect(() => {
    //   async function fetchTypeUser() {
    //     const response = await fetch("https://api-rest-luis-r45f.vercel.app/usertype");
    //     const data = await response.json();
    //     setTypeUser(data);
    //   }
    //   fetchTypeUser();
    // }, []);

  const validaciones = () => {
    const mayusculas = /[A-Z]/;
    const minusculas = /[a-z]/;
    const numeros = /\d/;
    const caracteres = /[!@#$%^&*()\-_=+{};:,<.>]/;

    const errors = [];

    if (!mayusculas.test(password)) {
      errors.push("La contraseña debe tener al menos una letra mayúscula.");
    }

    if (!minusculas.test(password)) {
      errors.push("La contraseña debe tener al menos una letra minúscula.");
    }

    if (!numeros.test(password)) {
      errors.push("La contraseña debe tener al menos un número.");
    }

    if (!caracteres.test(password)) {
      errors.push("La contraseña debe tener al menos un carácter especial.");
    }

    if (errors.length > 0) {
      const errorMessage = errors.join(" ");

      // setPasswordError(errors.join(" "));
      Swal.fire({
        title: "Error",
        text: errorMessage,
        icon: "error",
        confirmButtonText: "Cerrar",
      });

      return false;
    } else {
      setPasswordError("");
      return true;
    }
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
      if(validaciones()){
      const response = await fetch("https://api-rest-luis-r45f.vercel.app/users", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name: name, password: password, email: email}),
      });
      const data = await response.json();
      console.log(data);
      Swal.fire({
        title: "Insertado",
        text: "Documento insertado",
        icon: "success",
        confirmButtonText: "Cerrar",
      });
      guardarRecuperado((r) => !r);
      cerrarComponente();
    }
  };

  return (
    <div>
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          className="form-control"
          required 
          maxLength="28"
          minLength="3"
          value={name}
          onChange={(event) => setName(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Password:</label>
        <input
          type="password"
          className="form-control"
          required maxLength="28" minLength="8"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Email:</label>
        <input
          type="email"
          className="form-control"
          required 
          maxLength="28"
          minLength="3"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        />
      </div>
      <br/>
      <button type="submit" className="btn btn-primary">
        Agregar Usuario
      </button>
    </form>
    <br/>
    <button type="submit" className="btn btn-primary" onClick={()=>{cerrarComponente()}}>
        Regresar
      </button>
    </div>
  )
};

export default AgregarUsuario;
