import React, { useState } from "react";
import Header from '../estructura/Header'
import Footer from '../estructura/Footer'
import img1 from '../imagenes/carrusel/1.1.jpg'
import img2 from '../imagenes/carrusel/1.2.jpg'
import img3 from '../imagenes/carrusel/1.3.jpg'
import img4 from '../imagenes/carrusel/1.4.jpg'
import img5 from '../imagenes/carrusel/1.5.jpg'


import '../estilos/slider.css'


const index = () => {
  const [slideIndex, setSlideIndex] = useState(1);

  function currentSlide(n) {
    setSlideIndex(n);
  }

  function plusSlides(n) {
    setSlideIndex((prevSlideIndex) => prevSlideIndex + n);
  }

  function showSlides() {
    const slides = document.getElementsByClassName("slideC");
    const dots = document.getElementsByClassName("dotC");
    let i;

    if (slideIndex > slides.length) {
      setSlideIndex(1);
    }
    if (slideIndex < 1) {
      setSlideIndex(slides.length);
    }

    for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }
    for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" activeC", "");
    }
    slides[slideIndex - 1].style.display = "block";
    dots[slideIndex - 1].className += " activeC";
  }

  React.useEffect(() => {
    showSlides();
  }, [slideIndex]);
  return (
    <div>
      <Header />
      <div class="carouselC">
        <div class="carousel-wrapperC">
          <div class="slideC">
            <img src={ img1 } alt="Image 1" class="imgC" />
          </div>
          <div class="slideC">
            <img src={ img2 } alt="Image 2" class="imgC" />
          </div>
          <div class="slideC">
            <img src={ img3 } alt="Image 3" class="imgC" />
          </div>
          <div class="slideC">
            <img src={ img4 } alt="Image 4" class="imgC" />
          </div>
          <div class="slideC">
            <img src={ img5 } alt="Image 5" class="imgC" />
          </div>
        </div>
        <button class="prevC" onClick={() => plusSlides(-1)}>&#10094; Izquierda</button>
        {/* <button class="nextC" onClick={() => plusSlides(1)}>Derecha &#10095;</button> */}
        <div class="dots-wrapperC">
          <span class="dotC" onClick={() => currentSlide(1)}></span>
          <span class="dotC" onClick={() => currentSlide(2)}></span>
          <span class="dotC" onClick={() => currentSlide(3)}></span>
          <span class="dotC" onClick={() => currentSlide(4)}></span>
          <span class="dotC" onClick={() => currentSlide(5)}></span>
        </div>
      </div>
      
      <Footer />
    </div>
  )
}

export default index
