import React from 'react'
import Header from '../../estructura/Header'
import Footer from '../../estructura/Footer'
import './Login.css';

const Login = () => {

    const validar = async () =>{

        // if(email=='' || psw==''){
        //     showToast("Debe rellenar todos los campos")
        // }else{
        //     try {
        //         const res = await fetch('https://api-rest-proyecto.onrender.com/valUsers',{
        //             method: 'POST',
        //             headers: {
        //                 'Content-Type': 'application/json'
        //             },
        //             body: JSON.stringify({
        //                 email: email,
        //                 password: psw
        //             })
        //         })
        //         const data = await res.json()
        //         if(data.message=='Correo o contraseña incorrectos'){
        //             showToast("Correo o contraseña incorrectos")
        //         }else{
        //             navigation.navigate('Tabs')
        //         }
        //     } catch (error) {
        //         console.error(error)
        //     }
        // }
    }

    const [state, setState] = React.useState({
        form: {
          email: "",
          password: ""
        },
        error: false,
        errorMsg: ""
      });

    const manejadorSubmit= e =>{
        e.preventDefault();
    };

    const manejadorOnChange = async e =>{
        await setState({
            form:{
                ...state.form,
                [e.target.name]: e.target.value
            }
        })
        console.log(state.form);

    }

return (
<div>
    <Header />
        <div className="content">
            <div className="contact-wrapper animated bounceInUp">
                <div className="contact-form">
                    {/* <!-- action="validacion.php" --> */}
                    <form onSubmit={ manejadorSubmit }>
                        <h3>Comentario personal </h3>
                        <p className="box">
                            <label htmlFor="usuario">Usuario</label>
                            <input placeholder="Ingresa tu nombre" type="text" name="usuario"  onChange={ this.manejadorOnChange } />
                        </p>
                        <p className="box">
                            <label htmlFor="contraseña">Contraseña</label>
                            <input placeholder="Ingresa tu contraseña" type="password" name="pass" onChange={ this.manejadorOnChange } />
                        </p>
                        <br />
                        <p className="box form ">
                            <button type="submit" values="Login" onClick={()=>{ 
                                validar();
                            }}>Enviar</button>
                        </p>
                    </form>
                </div>
            </div>
        </div>
    <Footer />
</div>
)}

export default Login
