import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import Header from "../estructura/Header";
import Footer from "../estructura/Footer";
import Swal from 'sweetalert2';


const BodyCorreo = {
  backgroundColor: "#343a40",
  color: "#fff",
  paddingTop: "5rem",
};

const cardCorreo = {
  backgroundColor: "#424242",
  borderColor: "#333",
  borderRadius: "1rem",
  boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
  padding: "2rem",
  margin: "auto",
  maxWidth: "25rem",
};

const card_headerCorreo = {
  borderBottom: "none",
  textAlign: "center",
};

const card_footerCorreo = {
  borderTop: "none",
  textAlign: "center",
};

const form_controlCorreo = {
  backgroundColor: "#545454",
  border: "none",
  borderRadius: "0.5rem",
  color: "#fff",
  marginBottom: "1rem",
  padding: "0.75rem",
};

const btn_primaryCorreo = {
  backgroundColor: "#007bff",
  borderColor: "#007bff",
  borderRadius: "0.5rem",
  fontSize: "1rem",
  fontWeight: "600",
  padding: "0.75rem 2rem",
};

const CheckPass = () => {

  const navigate = useNavigate();

  const location = useLocation();
  const code = location.state;

  // const [id, setId] = useState(code.id);
  const id = code.id;

  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');

  const validaciones = () => {
    const mayusculas = /[A-Z]/;
    const minusculas = /[a-z]/;
    const numeros = /\d/;
    const caracteres = /[!@#$%^&*()\-_=+{};:,<.>]/;

    const errors = [];

    if (!mayusculas.test(password)) {
      errors.push("La contraseña debe tener al menos una letra mayúscula.");
    }

    if (!minusculas.test(password)) {
      errors.push("La contraseña debe tener al menos una letra minúscula.");
    }

    if (!numeros.test(password)) {
      errors.push("La contraseña debe tener al menos un número.");
    }

    if (!caracteres.test(password)) {
      errors.push("La contraseña debe tener al menos un carácter especial.");
    }

    if (password != password2) {
      errors.push("Las contraseñas no coinciden.");
    }

    if (errors.length > 0) {
      const errorMessage = errors.join(" ");

      // setPasswordError(errors.join(" "));
      Swal.fire({
        title: "Error",
        text: errorMessage,
        icon: "error",
        confirmButtonText: "Cerrar",
      });

      return false;
    } else {
      setPasswordError("");
      return true;
    }
  }

  const handlePass = async (event) => {
    event.preventDefault();


    if (validaciones()) {
      const respuesta = await fetch(`https://api-rest-luis-r45f.vercel.app/users/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          password: password
        })
      });

      const data = await respuesta.json();
      console.log(data);

      Swal.fire({
        title: "Actualizado",
        text: "Recuperacion terminda",
        icon: "success",
        confirmButtonText: "Cerrar",
      });
    };
  }

    return (
      <div>
        <Header />
        <div style={BodyCorreo}>
          <div class="container">
            <div class="row justify-content-center">
              <div class="col-lg-6">
                <div style={cardCorreo}>
                  <div style={card_headerCorreo}>
                    <h4>Restablece tu contraseña</h4>
                    <p>id: {id}</p>
                    {/* <p>id: {code.code}</p> */}
                  </div>
                  <div class="card-body">
                    <form onSubmit={handlePass}>
                      <div class="form-group">
                        <label for="correo">
                          Ingresa la nueva contraseña
                        </label>
                        <input
                          type="emial"
                          name="correo"
                          style={form_controlCorreo}
                          id="correo"
                          aria-describedby="emailHelp"
                          placeholder="Ingrese su correo electronico"
                          value={password}
                          onChange={(event) => setPassword(event.target.value)}
                        />
                      </div>
                      <div class="form-group">
                        <label for="correo">
                          Confirma la nueva contraseña
                        </label>
                        <input
                          type="emial"
                          name="correo"
                          style={form_controlCorreo}
                          id="correo"
                          aria-describedby="emailHelp"
                          placeholder="Ingrese su correo electronico"
                          value={password}
                          onChange={(event) => setPassword(event.target.value)}
                        />
                      </div>

                      <button type="submit" class="btn btn-primary btn-block">
                        Verificar
                      </button>
                    </form>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  };
  export default CheckPass;
