import React, { useState, useEffect } from "react";
import "./style.css";
import Header from "../../estructura/Header";
import Footer from "../../estructura/Footer";
// import 'bootstrap/dist/css/bootstrap.min.css';

const CrudProductos = () => {
  const styleBody = {
    backgroundColor: "#7A0244",
    color: "#fff",
    paddingTop: "5rem",
    paddingBottom: "5rem",
  };

  const card = {
    backgroundColor: "#424242",
    borderColor: "#333",
    borderRadius: "1rem",
    boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.3)",
    padding: "2rem",
    margin: "auto",
    maxWidth: "65rem",
  };

  const cardHeader = {
    borderBottom: "none",
    textAlign: "center",
  };

  const [articulos, setArticulos] = useState([]);
  const [recuperado, setRecuperado] = useState(false);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    fetch("https://api-rest-luis-r45f.vercel.app/products")
      .then((response) => {
        return response.json();
      })
      .then((articulos) => {
        setArticulos(articulos);
        setRecuperado(true);
        setCargando(false); // establecer cargando a falso después de que se obtienen los datos
      });
  }, []);

  // UPDATE

  const handleUpdate = (id) => {
    // Aquí es donde realizaremos la solicitud de actualización
  }

  

  return (
    <div>
      <Header />
      <div style={styleBody}>
        <div style={card}>
          <div style={cardHeader}>
            <h1>Tabla de PRODUCTOS</h1>
          </div>
          {cargando ? ( // mostrar mensaje de carga si cargando es verdadero
            <div className="row pt-3 mx-auto justify-content-center">
              <div className="col-8">
                <p>Cargando...</p>
              </div>
            </div>
          ) : (
            <div className="container">
              <div className="row mx-auto justify-content-between">
                <div className="col-3">
                  <a
                  // href="<?= base_url('/agregarProd')?>"
                  className="btn btn-success"
                  role="button"
                  >
                    Añadir +
                  </a>
                </div>
              </div>

              <div className="card-body">
                <div className="row pt-3 mx-auto justify-content-center">
                  <div className="col-8">
                    <div className="table-responsive">
                      <table className="table">
                        <thead className="thead-dark">
                          <tr>
                            <th scope="col">Nombre</th>
                            <th scope="col">Precio</th>
                            <th scope="col">Categoria</th>
                            <th></th>
                            <th></th>
                          </tr>
                        </thead>
                        <tbody>
                          {articulos.map((art) => {
                            return (
                              <tr>
                                <td>{art.name}</td>
                                <td>{art.price}</td>
                                <td>{art.categorie}</td>
                                <td>
                                <a
                                  className="btn btn-warning"
                                  role="button"
                                  onClick={() => handleUpdate(art._id)}
                                >
                                  Editar
                                </a>

                                </td>
                                <td>
                                  <a
                                  className="btn btn-danger"
                                  // href=""
                                  role="button"
                                  >
                                    Eliminar
                                  </a>
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default CrudProductos;
