import React, { useState, useEffect } from "react";
import Footer from "../../estructura/Footer";
import Header from "../../estructura/Header";
import "./Platillos.css";

import { useNavigate } from 'react-router-dom';

const Platillos = () => {
  const [comidas, setComidas] = useState([]);

  const navigate = useNavigate();

  async function response() {
    const response = await fetch("https://api-rest-luis-r45f.vercel.app/getProductsCategorie", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      //   body: JSON.stringify({ correo, password })
      body: JSON.stringify({
        categorie: "Comidas"
      }),
    });
    const data = await response.json();
    // console.log(data);
    setComidas(data);
  }

  

  useEffect(() => {
    response();
  }, []);


  const codigo = ({ id, code }) => {
    navigate("/checkcode", { state: { id, code } });
  };

  const handleProducto = async (event) => {
    event.preventDefault();
    const response = await fetch(
      "https://api-rest-luis-r45f.vercel.app/recuPassword",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: correo,
        }),
      }
    );

    const data = await response.json();
    console.log(data);

    if (
      data.message === "El correo electrónico proporcionado no está registrado"
    ) {
      console.log("El correo electrónico proporcionado no está registrado");
      Swal.fire({
        title: "Error",
        text: "El correo electrónico proporcionado no está registrado",
        icon: "error",
        confirmButtonText: "Cerrar",
      });
    } else {
      const response2 = await fetch(
        "https://api-rest-luis-r45f.vercel.app/usersEmail",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            email: correo,
          }),
        }
      );

      const data2 = await response2.json();
      codigo({ id: data2._id, code: data2.resetToken });
    }
  };

  return (
    <div>
      <Header />
      <h1 className="titlePlatillos">Platillos</h1>
      <div className="containerPlatillos">
        {comidas.map((art) => {
          return (
            <div className="cardPlatillos">
              <img
                src={art.image.secure_url }
                alt=""
                className="imgPlatillos"
              />
              {/* <h4>{art._id}</h4> */}
              <h4 className="h4Platillos">{art.name.slice(0, 20)}...</h4>
              <p className="pPlatillos">{art.description.slice(0, 170)}...</p>
              <a className="aPlatillos" onClick={handleProducto}>Ver más...</a>
            </div>
          );
        })}
      </div>
      <br /> <br />
      <Footer />
    </div>
  );
};

export default Platillos;
