import React, { useState, useEffect } from "react";
import Footer from "../../estructura/Footer";
import Header from "../../estructura/Header";
import { useLocation } from 'react-router-dom';

const idPlatillo = () => {
  const [comidas, setComidas] = useState([]);

  const location = useLocation();
  const id = location.state;

  async function response() {
    const response = await fetch(`https://api-rest-luis-r45f.vercel.app/products/${id}`)
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   //   body: JSON.stringify({ correo, password })
    //   body: JSON.stringify({
    //     categorie: "Comidas"
    //   }),
    // });
    const data = await response.json();
    // console.log(data);
    setComidas(data);
  }

  useEffect(() => {
    response();
  }, []);

  return (
    <div>
      <Header />
      <h1 className="titlePlatillos">Platillos</h1>
      <div className="containerPlatillos">
        {comidas.map((art) => {
          return (
            <div className="cardPlatillos">
              <img
                src={art.image.secure_url}
                alt=""
                className="imgPlatillos"
              />
              {/* <h4>{art._id}</h4> */}
              <h4 className="h4Platillos">{art.name.slice(0, 20)}...</h4>
              <p className="pPlatillos">{art.description.slice(0, 170)}...</p>
              <a className="aPlatillos">Ver m√°s...</a>
            </div>
          );
        })}
      </div>
      <br /> <br />
      <Footer />
    </div>
  );
};
export default idPlatillo
